import React, { Component } from 'react';
import Calendar from 'react-calendar';
import logo from './logo.svg';
import './App.css';
import firebase from './firebase.js';

class App extends Component {
  constructor() {
	  super();
	  this.state = {
		  date: new Date(),
		  test: '',
		  items: [],
		  currentItem: '',
		  username: ''
	  };
	  this.handleChange = this.handleChange.bind(this);
	  this.handleSubmit = this.handleSubmit.bind(this);
  }

  handleChange(e) {
	  this.setState({
		  [e.target.name]: e.target.value
	  });
  }

	componentDidMount() {
	  const itemsRef = firebase.database().ref('items');
	  itemsRef.on('value', (snapshot) => {
		let items = snapshot.val();
		let newState = [];
		for (let item in items) {
		  newState.push({
			id: item, //taskID
			title: items[item].title, //taskTitle
		  });
		}
		this.setState({
		  items: newState
		});
	  });
	}

  onChange = date => this.setState({date})

	  handleSubmit(e) {
		  e.preventDefault();
		  const itemsRef = firebase.database().ref('items');
		  const item = {
			title: this.state.username,
		  }
		  itemsRef.push(item);
		  this.setState({
			username: ''
		  });
	}

  render() {
    return (
      <div className="App">
        <header className="Calendar">
		<center>
		<Calendar 
			onChange={this.onChange}
			value={this.state.date}
			calendarType="US"
			onClickDay={(value)=>alert('Clicked day: ',value)}
		/>
		</center>
        </header>
		<div className="Container">
			<section className="add-item">
			{this.state.date}
				<form onSubmit={this.handleSubmit}>
					<input type="text" name="username" placeholder="Test connection" onChange={this.handleChange} value={this.state.username}/>
					<button>Add Item</button>
					<section className='display-item'>
					<div className='wrapper'>
					<ul>
					{this.state.items.map((item) => {
						return (
						<li key={item.id}>
							<h3>{item.id}</h3>
							<p>brought by: {item.username}</p>
						</li>
						)
					})}
					</ul>
					</div>
					</section>
				</form>
			</section>
			<section className="display-item">
				<div className="wrapper">
					<ul></ul>
				</div>
			</section>
		</div>
      </div>
    );
  }
}

export default App;
