import React, { Component } from 'react';
import Calendar from 'react-calendar';
import logo from './logo.svg';
import './App.css';
import firebase from './firebase.js';

class App extends Component {
  constructor() {
	  super();
	  this.state = {
		  date: new Date(),
		  habits: [],
		  name: '',
		  completed: '',
		  notes: '',
		  type: '',
		  test: 'Spend less'
	  };
	  this.handleChange = this.handleChange.bind(this);
	  this.handleSubmit = this.handleSubmit.bind(this);
  }

  handleChange(e) {
	  this.setState({
		  [e.target.name]: e.target.value
	  });
  }

  componentDidMount() {
	  const habitsRef = firebase.database().ref('users/a@gmail_com/history/2019-04-16/'+test); //retrieve 'users' from firebase
	  habitsRef.on('value', (snapshot) => {
		let habits = snapshot.val();
		let newState = [];
		for (let habit in habits) {
		  newState.push({
			id: habit,
			screen: habits.habit
		  });
		}
		this.setState({
		  habits: newState
		});
	  });
  }

  onChange = date => this.setState({date})

  handleSubmit(e) {
	  e.preventDefault(); //use to prevent refreshing
	  const habitsRef = firebase.database().ref('users');
	  const habit = {
		name: this.state.username,
	  }
	  habitsRef.push(habit); //push item to database
	  this.setState({
		username: '' //clear out inputs
	  });
  }

  render() {
    return (
      <div className="App">
        <header className="Calendar">
		<center>
		<Calendar 
			onChange={this.onChange}
			value={this.state.date}
			calendarType="US"
			onClickDay={(value)=>alert('Clicked day: ',value)}
		/>
		</center>
        </header>
		<div className="Container">
			<section className="add-item">
			<section className="display-date">
			<center><td>{this.state.date.toLocaleDateString()}</td></center>
			</section>
				<form onSubmit={this.handleSubmit}>
					<input type="text" name="username" placeholder="Test connection" onChange={this.handleChange} value={this.state.username}/>
					<button>Add Item</button>
					<section className='display-item'>
					<div className='wrapper'>
					<ul>
					{this.state.habits.map((habit) => {
						return (
						<li key={habit.id}>
							<h3>{habit.id}</h3>
							<p>brought by: {habit.screen}</p>
						</li>
						)
					})}
					</ul>
					</div>
					</section>
				</form>
			</section>
			<section className="display-item">
				<div className="wrapper">
					<ul></ul>
				</div>
			</section>
		</div>
      </div>
    );
  }
}

export default App;
