import React, { Component } from 'react';
import Calendar from 'react-calendar';
import './App.css';
import firebase from './firebase.js';

class App extends Component {
  constructor() {
	  super();
	  this.state = {
		  date: new Date(),
		  habits: [],
		  subtasks: [],
		  name: '',
		  completed: '',
		  notes: '',
		  type: ''
	  };
	  this.handleChange = this.handleChange.bind(this);
	  this.handleSubmit = this.handleSubmit.bind(this);
  }

  handleChange(e) {
	  this.setState({
		  [e.target.name]: e.target.value
	  });
  }

  componentDidMount() {
	  var habitName = '';
	  var habitProp = '';
	  const habitsRef = firebase.database().ref('users/a@gmail_com/history/2019-04-16/'); //retrieve 'users' from firebase
	  habitsRef.on('value', (snapshot) => {
		let habits = snapshot.val();
		let newState = [];
		for (let habit in habits) {
		  habitName = habit;
		  //retrieve task name
		  newState.push({
			id: habit,
		  });

		  //retrieve habit property names
		  const ref = firebase.database().ref('users/a@gmail_com/history/2019-04-16/'+habitName);
		  ref.on('value', (snapshot) => {
			  let habits = snapshot.val();
			  for (let habit in habits) {
				  if (habit !== 'completed') {
				  habitProp = habit;
				  newState.push({
					  screen: habit,
				  });

				  //retrieve habit property values
				  const subRef = firebase.database().ref('users/a@gmail_com/history/2019-04-16/'+habitName+'/'+habitProp);
				  subRef.on('value', (snapshot) => {
					  let habits = snapshot.val();
					  for (let habit in habits) {
						  newState.push({
							  property: habit,
						  });
					  }
				  })
			  }}
		  })
		}
		this.setState({
		  habits: newState
		});
	  });
  }

  onChange = date => this.setState({date})

  handleSubmit(e) {
	  e.preventDefault(); //use to prevent refreshing
	  const habitsRef = firebase.database().ref('users');
	  const habit = {
		name: this.state.username,
	  }
	  habitsRef.push(habit); //push item to database
	  this.setState({
		username: '' //clear out inputs
	  });
  }

  render() {
    return (
      <div className="App">
        <header className="Calendar">
		<center>
		<Calendar 
			onChange={this.onChange}
			value={this.state.date}
			calendarType="US"
			onClickDay={(value)=>alert('Clicked day: ',value)}
		/>
		</center>
        </header>
		<div className="Container">
			<section className="add-item">
			<section className="display-date">
			<center><td>{this.state.date.toLocaleDateString()}</td></center>
			</section>
				<form onSubmit={this.handleSubmit}>
					<input type="text" name="username" placeholder="Test connection" onChange={this.handleChange} value={this.state.username}/>
					<button>Add Item</button>
					<section className='display-item'>
					<div className='wrapper'>
					<ul>
					{this.state.habits.map((habit) => {
						return (
						<li key={habit.id}>
							<h3>{habit.id}</h3>
							<p>brought by: {habit.screen}</p>
							<p>Additionally: {habit.property}</p>
						</li>
						)
					})}
					</ul>
					</div>
					</section>
				</form>
			</section>
			<section className="display-item">
				<div className="wrapper">
					<ul></ul>
				</div>
			</section>
		</div>
      </div>
    );
  }
}

export default App;
