import React, { Component } from 'react';
import Calendar from 'react-calendar';
import './App.css';
import firebase from './firebase.js';

class App extends Component {
  constructor() {
	  super();
	  this.state = {
		  date: new Date(),
		  habit: [],
		  completed: [],
		  notes: [],
		  type: [],
		  post: '',
		  email: '',
		  loading: true
	  };
	  this.handleSubmit = this.handleSubmit.bind(this);
  }

  componentDidMount() {
	  const ref = firebase.database().ref('users/a@gmail_com/history');
	  const post = ref.child('2019-04-16').orderByKey();

	  post.once('value', snap => {
		  snap.forEach(child => {
			  this.setState({
				  habit: this.state.habit.concat([child.key]),
				  completed: this.state.completed.concat([child.val().completed]),
				  notes: this.state.notes.concat([child.val().notes]),
				  type: this.state.type.concat([child.val().type])
			  });

			  const postList = this.state.habit.map((dataList, index) =>
				<p>
				{dataList}
				<br />
				Completed: {this.state.completed[index]}
				<br />
				Notes: {this.state.notes[index]}
				<br />
				Type: {this.state.type[index]}
				<hr />
				</p>
			  );

			  this.setState({
				  post: postList,
				  loading: false
			  });
		  });
		}); 
	}

  onChange = date => this.setState({date})

  handleSubmit(e) {
	  e.preventDefault(); //use to prevent refreshing
	  const habitsRef = firebase.database().ref('users');
	  const habit = {
		name: this.state.username,
	  }
	  habitsRef.push(habit); //push item to database
	  this.setState({
		username: '' //clear out inputs
	  });
  }

  render() {
    return loading ? (
	  <div> loading... </div>
    ) : (
      <div className="App">
        <header className="Calendar">
		<center>
		<button class="button"><span>Momentum Tracker </span></button>
		<Calendar 
			onChange={this.onChange}
			value={this.state.date}
			calendarType="US"
			onClickDay={(value)=>alert('Clicked day: ',value)}
		/>
		</center>
        </header>
		<div className="Container">
			<section className="add-item">
			<section className="display-date">
			<center><td>{this.state.date.toLocaleDateString()}</td></center>
			</section>
			<div className="habits">
			<ul>{this.state.post}</ul>
			</div>
			</section>
		</div>
      </div>
    );
  }
}

export default App;
